    private void bnk_ekle_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bnk_ekle_btnActionPerformed
        String bankaciEskiKullanici=mdr_bnk_eskl_kll.getText();
        String bankaciKullanici=mdr_bnk_ekl_kll.getText();
        String bankaciPassword=mdr_bnk_ekl_sfr.getText();
        String bankaciAdi=mdr_bnk_ekl_adi.getText();
        String bankaciSoyadi=mdr_bnk_ekl_sadi.getText();
        String queryStringi="UPDATE bankaci SET bnk_kll='"+bankaciKullanici+"',bnk_sfr='"+bankaciPassword+"',bnk_adi='"+bankaciAdi+"',bnk_sifre='"+bankaciSoyadi+"' WHERE bnk_kll='"+bankaciEskiKullanici+"'";
        try {
            pst=conn.prepareStatement(queryStringi);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Bankacı düzenlendi.");

        }

        catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_bnk_ekle_btnActionPerformed

